// components/ImageUploader.jsx
import React, { useRef, useState } from "react";

const ImageUploader = ({ onImageSend }) => {
  const fileInputRef = useRef(null);
  const [preview, setPreview] = useState(null);

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setPreview(imageUrl);
      await sendImageToServer(file, imageUrl);
    }
  };

  const handleCameraClick = () => {
    fileInputRef.current.click();
  };

  const sendImageToServer = async (file, previewUrl) => {
    // הצגת התמונה בצ'אט דרך פונקציית האב
    onImageSend(previewUrl);

    // שליחת POST לשרת
    const formData = new FormData();
    formData.append("image", file);

    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/image`, {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const errorText = await res.text();
        console.error("Image upload error:", errorText);
      } else {
        console.log("Image uploaded successfully");
      }
    } catch (err) {
      console.error("Failed to send image:", err.message);
    }
  };

  return (
    <div style={{ margin: "1rem 0" }}>
      <button onClick={handleCameraClick}>📷 Upload / Take Picture</button>
      <input
        type="file"
        accept="image/*"
        capture="environment"
        ref={fileInputRef}
        style={{ display: "none" }}
        onChange={handleFileChange}
      />
    </div>
  );
};

export default ImageUploader;
