import React, { useState, useRef, useEffect } from "react";
import { FaMicrophone, FaStop, FaTrash, FaPlay, FaPause } from "react-icons/fa";

function VoiceRecorder({ onSendAudio }) {
  const [status, setStatus] = useState("idle"); // idle, recording, recorded
  const [isPaused, setIsPaused] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const [audioUrl, setAudioUrl] = useState(null);
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);

  // התחלת הקלטה
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      mediaRecorderRef.current = recorder;
      audioChunksRef.current = [];
      recorder.start();
      setStatus("recording");
      setIsPaused(false);

      recorder.ondataavailable = (e) => {
        audioChunksRef.current.push(e.data);
      };

      recorder.onstop = () => {
        const blob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        const url = URL.createObjectURL(blob);
        setAudioUrl(url);
        setStatus("recorded");
        setIsPaused(false);
      };

      recorder.onpause = () => {
        setIsPaused(true);
      };

      recorder.onresume = () => {
        setIsPaused(false);
      };
    } catch (error) {
      console.error("Error starting recording:", error);
    }
  };

  // עצירת הקלטה
  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
    }
  };

  // הפסקה להמשך הקלטה
  const pauseRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.pause();
    }
  };

  // המשך הקלטה אחרי הפסקה
  const resumeRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "paused") {
      mediaRecorderRef.current.resume();
    }
  };

  // איפוס הקלטה (גם אם מקליטים כרגע)
  const resetRecording = () => {
    if (audioUrl) {
      URL.revokeObjectURL(audioUrl);
    }
    setAudioUrl(null);
    setStatus("idle");
    setIsPaused(false);
    audioChunksRef.current = [];

    // אם מקליטים, נעצור את ההקלטה
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
      mediaRecorderRef.current.stop();
    }
  };

  // שליחת הקלטה דרך callback
  const sendRecording = () => {
    if (!audioChunksRef.current.length) return;
    const blob = new Blob(audioChunksRef.current, { type: "audio/webm" });
    onSendAudio(blob);
    resetRecording();
  };

  // ניהול ניגון
  const togglePlay = () => {
    if (!audioRef.current) return;
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
  };

  useEffect(() => {
    if (!audioRef.current) return;

    const handleEnded = () => setIsPlaying(false);
    const handlePlay = () => setIsPlaying(true);
    const handlePause = () => setIsPlaying(false);

    audioRef.current.addEventListener("ended", handleEnded);
    audioRef.current.addEventListener("play", handlePlay);
    audioRef.current.addEventListener("pause", handlePause);

    return () => {
      if (!audioRef.current) return;
      audioRef.current.removeEventListener("ended", handleEnded);
      audioRef.current.removeEventListener("play", handlePlay);
      audioRef.current.removeEventListener("pause", handlePause);
    };
  }, [audioUrl]);

  return (
    <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
      {status === "idle" && (
        <button onClick={startRecording} title="Start Recording" style={{ fontSize: "1.5rem" }}>
          <FaMicrophone color="red" />
        </button>
      )}

      {status === "recording" && (
        <>
          <button onClick={stopRecording} title="Stop Recording" style={{ fontSize: "1.5rem" }}>
            <FaStop color="red" />
          </button>
          <button
            onClick={isPaused ? resumeRecording : pauseRecording}
            title={isPaused ? "Resume Recording" : "Pause Recording"}
            style={{ fontSize: "1.5rem" }}
          >
            {isPaused ? <FaPlay /> : <FaPause />}
          </button>
          <button onClick={resetRecording} title="Reset Recording" style={{ color: "red", fontSize: "1.5rem" }}>
            <FaTrash />
          </button>
        </>
      )}

      {status === "recorded" && (
        <>
          <button onClick={togglePlay} title={isPlaying ? "Pause" : "Play"} style={{ fontSize: "1.5rem" }}>
            {isPlaying ? <FaPause /> : <FaPlay />}
          </button>
          <audio ref={audioRef} src={audioUrl} />
          <button onClick={sendRecording} title="Send Recording" style={{ fontSize: "1rem" }}>
            Send
          </button>
          <button onClick={resetRecording} title="Delete Recording" style={{ color: "red", fontSize: "1.5rem" }}>
            <FaTrash />
          </button>
        </>
      )}
    </div>
  );
}

export default VoiceRecorder;
