return (
  <Router>
    <Routes>
      <Route
        path="/"
        element={
          !showChat ? (
            <HomeScreen onStartChat={() => setShowChat(true)} />
          ) : (
            <div style={{ maxWidth: "600px", margin: "0 auto", padding: "2rem" }}>
              <h1>ResQPal Chat</h1>
              <ChatWindow messages={messages} />
              <MessageInput
                inputMsg={inputMsg}
                setInputMsg={setInputMsg}
                onSend={sendRequest}
                disabled={isLoading || isFinalDecision}
              />
              {!isFinalDecision && (
                <VoiceRecorder onSendAudio={handleSendAudio} />
              )}
              {ambulance_flag && isFinalDecision && !locationSent && (
                <LocationFetcher onLocation={handleLocation} />
              )}
              {showImageCapture && (
                <ImageCapture
                  burnsDetected={burnsDetected}
                  onCancel={() => {
                    setShowImageCapture(false);
                    setBurnsDetected(null);
                  }}
                  onCapture={(result) => {
                    // ניתן לעדכן את messages עם תוצאת הניתוח מהתמונה
                    setMessages((prev) => [
                      ...prev,
                      { text: `Image result: ${result}`, fromUser: false },
                    ]);
                    setShowImageCapture(false);
                  }}
                />
              )}
            </div>
          )
        }
      />
      <Route path="/treatment" element={<TreatmentScreen />} />
    </Routes>
  </Router>
);

