import React from "react";

const ChatWindow = ({ messages }) => {
  return (
    <div
      style={{
        border: "1px solid #ccc",
        borderRadius: "8px",
        padding: "1rem",
        height: "400px",
        overflowY: "auto",
        backgroundColor: "#f9f9f9",
        marginBottom: "1rem",
      }}
    >
      {messages.length === 0 && (
        <p style={{ textAlign: "center", color: "#888" }}>No messages yet</p>
      )}
      {messages.map((msg, index) => (
        <div
          key={index}
          style={{
            marginBottom: "1rem",
            textAlign: msg.fromUser ? "right" : "left",
          }}
        >
          {msg.isAmbulanceAlert && (
            <div
              style={{
                backgroundColor: "#ffdddd",
                color: "#b30000",
                border: "2px solid red",
                borderRadius: "10px",
                padding: "12px",
                fontWeight: "bold",
                maxWidth: "80%",
                margin: msg.fromUser ? "0 0 0 auto" : "0 auto 0 0",
              }}
            >
              ðŸš¨ Emergency! Ambulance needed
            </div>
          )}
          {msg.text && (
            <p
              style={{
                display: "inline-block",
                backgroundColor: msg.fromUser ? "#DCF8C6" : "#EAEAEA",
                color: "#333",
                padding: "8px 12px",
                borderRadius: "15px",
                maxWidth: "80%",
                wordWrap: "break-word",
                margin: 0,
              }}
            >
              {msg.text}
            </p>
          )}
          {msg.audioUrl && (
            <audio
              controls
              src={msg.audioUrl}
              style={{ marginTop: "5px", maxWidth: "80%" }}
            />
          )}
        </div>
      ))}
    </div>
  );
};

export default ChatWindow;
