import React, { useRef, useState, useEffect, useCallback } from "react";

const ImageUploader = ({ onImageSend }) => {
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [preview, setPreview] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [stream, setStream] = useState(null);

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setPreview(imageUrl);
      await sendImageToServer(file, imageUrl);
    }
  };

  const handleOpenFileDialog = () => {
    fileInputRef.current.click();
  };

  const handleStartCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: true,
      });
      setStream(mediaStream);
      setShowCamera(true);
    } catch (err) {
      console.error("Failed to access camera:", err);
      alert("Camera access failed. Please allow camera permissions.");
    }
  };

  useEffect(() => {
    if (showCamera && videoRef.current && stream) {
      videoRef.current.srcObject = stream;
    }
  }, [showCamera, stream]);

  const handleTakePhoto = async () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    canvas.toBlob(async (blob) => {
      if (!blob) {
        console.error("Failed to capture image from camera");
        return;
      }
      const imageUrl = URL.createObjectURL(blob);
      setPreview(imageUrl);
      await sendImageToServer(blob, imageUrl);
      stopCamera();
    }, "image/jpeg");
  };

  const stopCamera = useCallback(() => {
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
    }
    setShowCamera(false);
    setStream(null);
  }, [stream]);

  const sendImageToServer = async (fileOrBlob, previewUrl) => {
    onImageSend(previewUrl);

    const formData = new FormData();
    formData.append("image", fileOrBlob);

    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/upload-image`, {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const errorText = await res.text();
        console.error("Image upload error:", errorText);
        alert("Error uploading image: " + errorText);
      } else {
        const result = await res.json();
        console.log("Server response:", result);
      }
    } catch (err) {
      console.error("Failed to send image:", err.message);
      alert("Upload failed: " + err.message);
    }
  };

  useEffect(() => {
    return () => stopCamera(); // cleanup on unmount
  }, [stopCamera]);

  return (
    <div style={{ textAlign: "center", padding: "1rem" }}>
      <button onClick={handleOpenFileDialog}>üìÅ Upload from Computer</button>
      <button onClick={handleStartCamera}>üì∑ Take a Picture</button>

      <input
        type="file"
        accept="image/*"
        ref={fileInputRef}
        style={{ display: "none" }}
        onChange={handleFileChange}
      />

      {showCamera && (
        <div style={{ marginTop: "1rem" }}>
          <video
            ref={videoRef}
            autoPlay
            playsInline
            style={{ width: "100%", maxWidth: "400px", borderRadius: "12px" }}
          />
          <div style={{ marginTop: "1rem" }}>
            <button onClick={handleTakePhoto}>üì∏ Capture</button>
            <button onClick={stopCamera} style={{ marginLeft: "1rem" }}>
              ‚ùå Cancel
            </button>
          </div>
        </div>
      )}

      <canvas ref={canvasRef} style={{ display: "none" }} />

      {preview && (
        <div style={{ marginTop: "1rem" }}>
          <strong>Preview:</strong>
          <br />
          <img
            src={preview}
            alt="Preview"
            style={{ maxWidth: "100%", border: "1px solid #ccc", borderRadius: "8px" }}
          />
        </div>
      )}
    </div>
  );
};

export default ImageUploader;


