 const sendImageToServer = async (fileOrBlob, previewUrl) => {
    try {
      const formData = new FormData();
      formData.append("image", fileOrBlob, "image.jpg");

      const res = await fetch(`${process.env.REACT_APP_API_URL}/upload_image`, {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`Upload failed: ${errorText}`);
      }

      const data = await res.json();

      // data.imageUrl זה כתובת התמונה שהשרת החזיר - צריך שהשרת יחזיר משהו כזה
      if (onUploadSuccess) {
        onUploadSuccess(data.imageUrl || previewUrl);
      }
    } catch (error) {
      console.error("Error uploading image:", error);
      alert("Error uploading image: " + error.message);
    }
  };

  useEffect(() => {
    return () => stopCamera(); // Clean up on unmount
  }, [stopCamera]);

   return (
    <div>
      {!showCamera ? (
        <>
          <button onClick={handleOpenFileDialog}>Upload Image</button>
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            style={{ display: "none" }}
            onChange={handleFileChange}
          />
          <button onClick={handleStartCamera}>Take Photo</button>
          {preview && <img src={preview} alt="Preview" style={{ maxWidth: "100%" }} />}
          <button onClick={onCancel}>Cancel</button>
        </>
      ) : (
        <div>
          <video ref={videoRef} style={{ width: "100%" }} />
          <button onClick={handleTakePhoto}>Capture Photo</button>
          <button onClick={stopCamera}>Stop Camera</button>
          <canvas ref={canvasRef} style={{ display: "none" }} />
        </div>
      )}
    </div>
  );
};

export default ImageUploader;
