import React, { useEffect, useState } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";

function TreatmentScreen() {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const caseType = searchParams.get("case_type");
  const degree = searchParams.get("degree");
  const initialCount = parseInt(searchParams.get("count")) || 0;
  const [count, setCount] = useState(initialCount);

  useEffect(() => {
    const fetchTreatment = async () => {
      try {
        const url = new URL(`${process.env.REACT_APP_API_URL}/treatment`);
        url.searchParams.append("case_type", caseType);
        url.searchParams.append("count", count);
        if (degree) {
          url.searchParams.append("degree", degree);
        }

        const res = await fetch(url);
        if (!res.ok) {
          throw new Error("Failed to fetch treatment instructions.");
        }
        const json = await res.json();
        setData(json.result);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchTreatment();
  }, [caseType, count, degree]);
  const handleDidntUnderstand = () => {
    if (count < 3) {
      const newCount = count + 1;
      setCount(newCount);
      setSearchParams((prevParams) => {
        const updated = new URLSearchParams(prevParams);
        updated.set("count", newCount);
        return updated;
      });
    }
  };
 
  return (
    <div style={{ maxWidth: "600px", margin: "0 auto", padding: "2rem" }}>
      <h2>Treatment Instructions</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {!error && !data && <p>Loading...</p>}

      {data && typeof data === "string" && <p>{data}</p>}

      {data && typeof data === "object" && data.type === "image" && (
        <img src={data.url} alt="treatment visual" style={{ maxWidth: "100%" }} />
      )}

      {data && typeof data === "object" && data.type === "video" && (
        <video controls style={{ maxWidth: "100%" }}>
          <source src={data.url} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      )}

      <div style={{ marginTop: "1.5rem" }}>
        <button onClick={() => navigate(-1)} style={{ marginRight: "1rem" }}>
          Back to chat
        </button>

        {/* כפתור "לא הבנתי" מוצג רק אם count קטן מ־3 */}
        {count < 3 && (
          <button onClick={handleDidntUnderstand}>
            לא הבנתי – נסו להסביר שוב
          </button>
        )}
      </div>
    </div>
  );
}

export default TreatmentScreen;
