import React, { useRef, useState, useEffect } from "react";

const ImageUploader = ({ onImageSend }) => {
  const fileInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [preview, setPreview] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [stream, setStream] = useState(null);
  const [isCapturing, setIsCapturing] = useState(false);

  // פתיחת קובץ מהמחשב
  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const imageUrl = URL.createObjectURL(file);
    setPreview(imageUrl);
    await sendImageToServer(file);
  };

  const handleOpenFileDialog = () => {
    fileInputRef.current.click();
  };

  // התחלת המצלמה
  const handleStartCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = mediaStream;
      setStream(mediaStream);
      setShowCamera(true);
    } catch (err) {
      console.error("Failed to access camera:", err);
      alert("Camera access denied or not available.");
    }
  };

  // צילום תמונה
  const handleTakePhoto = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    canvas.toBlob(async (blob) => {
      if (!blob) return;
      const imageUrl = URL.createObjectURL(blob);
      setPreview(imageUrl);
      await sendImageToServer(blob);
      stopCamera();
    }, "image/jpeg");

    setIsCapturing(true);
    setTimeout(() => setIsCapturing(false), 200); // אפקט קל של "פלאש"
  };

  // עצירת המצלמה
  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
    }
    setShowCamera(false);
  };

  // שליחת התמונה לשרת
  const sendImageToServer = async (fileOrBlob) => {
    const formData = new FormData();
    formData.append("image", fileOrBlob);

    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/image`, {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const errorText = await res.text();
        console.error("Image upload error:", errorText);
        alert("Upload failed");
      } else {
        console.log("Image uploaded successfully");
        onImageSend && onImageSend(URL.createObjectURL(fileOrBlob));
      }
    } catch (err) {
      console.error("Failed to send image:", err.message);
    }
  };

  useEffect(() => {
    return () => stopCamera(); // ניקוי בעת סיום
  }, []);

  return (
    <div style={{ textAlign: "center", maxWidth: "500px", margin: "auto" }}>
      <button onClick={handleOpenFileDialog}>📁 Upload from Computer</button>
      <button onClick={handleStartCamera} disabled={showCamera}>📷 Take a Picture</button>

      <input
        type="file"
        accept="image/*"
        ref={fileInputRef}
        style={{ display: "none" }}
        onChange={handleFileChange}
      />

      {showCamera && (
        <div style={{ position: "relative", marginTop: "1rem" }}>
          <video
            ref={videoRef}
            autoPlay
            playsInline
            muted
            style={{
              width: "100%",
              maxWidth: "100%",
              borderRadius: "8px",
              boxShadow: "0 0 10px rgba(0,0,0,0.3)",
              filter: isCapturing ? "brightness(200%)" : "none",
              transition: "filter 0.2s"
            }}
          />
          <button
            onClick={handleTakePhoto}
            style={{ marginTop: "1rem", padding: "0.5rem 1rem" }}
          >
            📸 Capture
          </button>
        </div>
      )}

      <canvas ref={canvasRef} style={{ display: "none" }} />

      {preview && (
        <div style={{ marginTop: "1.5rem" }}>
          <strong>Preview:</strong><br />
          <img
            src={preview}
            alt="Preview"
            style={{ width: "100%", maxWidth: "100%", borderRadius: "8px" }}
          />
        </div>
      )}
    </div>
  );
};

export default ImageUploader;
