import React, { useState, useRef, useEffect } from "react";

function ImageCapture({ onCapture, onCancel }) {
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);
  
  const handleImageCapture = async (blob) => {
  const formData = new FormData();
  formData.append("image", blob, "burn.jpg");



  useEffect(() => {
    async function startCamera() {
      try {
        const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true });

        if (videoRef.current) {
          videoRef.current.srcObject = mediaStream;

          videoRef.current.onloadedmetadata = () => {
            videoRef.current.play();
          };

          setStream(mediaStream);
        }
      } catch (err) {
        console.error('Error accessing camera:', err);
      }
    }

    startCamera();

    // פונקציית ניקוי – תעצור את המצלמה כאשר הרכיב יוצא
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, []); // ריק כדי שירוץ פעם אחת בלבד

  return (
    <div>
      <video
        ref={videoRef}
        style={{ width: '100%', maxWidth: '400px' }}
        autoPlay
        muted
        playsInline
      />
      <div style={{ marginTop: '1rem' }}>
        <button onClick={handleImageCapture}>take a pic</button>
        <button onClick={onCancel}>cancel</button>
      </div>
    </div>
  );
}
}
export default ImageCapture;
