import React, { useState, useRef, useEffect } from "react";
import { FaMicrophone, FaStop, FaTrash, FaPlay, FaPause } from "react-icons/fa";

function VoiceRecorder({ onSendAudio }) {
  const [status, setStatus] = useState("idle"); // idle, recording, recorded
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const [audioUrl, setAudioUrl] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  // התחלת הקלטה
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      mediaRecorderRef.current = recorder;
      audioChunksRef.current = [];
      recorder.start();
      setStatus("recording");

      recorder.ondataavailable = (e) => {
        audioChunksRef.current.push(e.data);
      };

      recorder.onstop = () => {
        const blob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        const url = URL.createObjectURL(blob);
        setAudioUrl(url);
        setStatus("recorded");
      };
    } catch (error) {
      console.error("Error starting recording:", error);
    }
  };

  // עצירת הקלטה
  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
    }
  };

  // מחיקת הקלטה
  const resetRecording = () => {
    if (audioUrl) {
      URL.revokeObjectURL(audioUrl);
    }
    setAudioUrl(null);
    setStatus("idle");
  };

  // שליחת הקלטה
  const sendRecording = () => {
    if (!audioChunksRef.current.length) return;
    const blob = new Blob(audioChunksRef.current, { type: "audio/webm" });
    onSendAudio(blob);
    resetRecording();
  };

  // ניהול נגינה
  const togglePlay = () => {
    if (!audioRef.current) return;
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
  };

  useEffect(() => {
    if (!audioRef.current) return;

    const handleEnded = () => setIsPlaying(false);
    audioRef.current.addEventListener("ended", handleEnded);
    audioRef.current.addEventListener("play", () => setIsPlaying(true));
    audioRef.current.addEventListener("pause", () => setIsPlaying(false));

    return () => {
      if (!audioRef.current) return;
      audioRef.current.removeEventListener("ended", handleEnded);
    };
  }, [audioUrl]);

  return (
    <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
      {status === "idle" && (
        <button onClick={startRecording} title="Start Recording" style={{ fontSize: "1.5rem" }}>
          <FaMicrophone color="red" />
        </button>
      )}
      {status === "recording" && (
        <button onClick={stopRecording} title="Stop Recording" style={{ fontSize: "1.5rem" }}>
          <FaStop color="red" />
        </button>
      )}
      {status === "recorded" && (
        <>
          <button onClick={togglePlay} title={isPlaying ? "Pause" : "Play"} style={{ fontSize: "1.5rem" }}>
            {isPlaying ? <FaPause /> : <FaPlay />}
          </button>
          <audio ref={audioRef} src={audioUrl} />
          <button onClick={sendRecording} title="Send Recording">
            Send
          </button>
          <button onClick={resetRecording} title="Delete Recording" style={{ color: "red" }}>
            <FaTrash />
          </button>
        </>
      )}
    </div>
  );
}

export default VoiceRecorder;
